import React , {Component} from 'react';
import { reactLocalStorage } from 'reactjs-localstorage';

const user = (reactLocalStorage.getObject('user'))

class AddBooks extends Component{
    constructor(props){
        super(props);
        this.state = {
            user_id: user.user,
            book_name: '',
            book_author: '',
            book_price: '',
            book_read: ''
        }
    }

    onBookNameChange = (event) => {
        this.setState({book_name: event.target.value})
    }

    onBookAuthorChange = (event) => {
        this.setState({book_author: event.target.value})
    }

    onBookPriceChange = (event) => {
        this.setState({book_price: event.target.value})
    }

    onBookReadChange = (event) => {
        this.setState({book_read: event.target.value})
    }

    onSubmit = () => {
        console.log(this.state.user_id)
        console.log(this.state.book_name)
        console.log(this.state.book_author)
        console.log(this.state.book_price)
        console.log(this.state.book_read)
    }

    render(){
    return(
    <form className="measure center">
        <h2 className="tc f2 black-70 coolFont">Add a new book</h2>
        <div className="mt3">
            <label className="db f6 ph2 mb2">Book Name</label>
            <input onChange={this.onBookNameChange} className="db w-100 b pv2 ph3 bg-light-blue hover-bg-lightest-blue hover-gray bn br-pill " type="text" />
        </div>
        <div className="mt3">
            <label className="db f6 ph2 mb2">Author</label>
            <input onChange={this.onBookAuthorChange} className="db w-100 b pv2 ph3 bg-light-blue hover-bg-lightest-blue hover-gray bn br-pill " type="text" />
        </div>
        <div className="mt3">
            <label className="db f6 ph2 mb2">Price</label>
            <input onChange={this.onBookPriceChange} className="db w-100 b pv2 ph3 bg-light-blue hover-bg-lightest-blue hover-gray bn br-pill " type="text" />
        </div>

        <div className="mt3">
        <select onChange={this.onBookReadChange} className="db w-100 b pv2 ph3 bg-light-blue hover-bg-lightest-blue hover-grey bn br-pill pa2 input-reset">
            <option value="not-read">Not read</option>
            <option value="read">Read</option>

        </select>
        </div>
        <div className="mt3">
            <p onClick={this.onSubmit} className="input-reset db w-100 f6 b ttu tracked pv3 ph3 pointer bg-light-blue hover-bg-lightest-blue bn br-pill" type="submit" value="Add book"/>
        </div>
    </form>
    );
    }
}

export default AddBooks;
