import React, { Component } from 'react';
import NavBar from '../components/NavBar';
import AddBooks from '../components/AddBooks';
import MyBooks from '../components/MyBooks';
import BooksToRead from "../components/BooksToRead";

const QUOTES = ['Reading gives us someplace to go when we have to stay where we are.','Always read something that will make you look good if you die in the middle of it.','Anyone who says they have only one life to live must not know how to read a book.','I think of life as a good book. The further you get into it, the more it begins to make sense.',''];
class App extends Component {
  constructor(){
    super();
    this.state = {
      route: ''
    }
  }

  render() {
    return (
      <div className="container helvetica">
          <NavBar/>
          {
              (this.state.route === 'ToRead') ? <BooksToRead/>
              : (this.state.route === 'addBook') ? <AddBooks/>
              : (this.state.route === 'MyBooks') ? <MyBooks/>
              :             <h2 className="tc ph4 f5 f4-m f3-l fw2 black-50 mt0 lh-copy">Books to read</h2>
          }


      </div>
    );
  }
}

export default App;
