import React , {Component} from 'react';
import { reactLocalStorage } from 'reactjs-localstorage';

const user = (reactLocalStorage.getObject('user'))

class AddBooks extends Component{
    render(){
    return(
    <form className="measure center">
        <h2 className="tc f2 black-70 coolFont">Add a new book</h2>
        <div className="mt3">
            <label className="db f6 ph2 mb2">Book Name</label>
            <input className="db w-100 b pv2 ph3 bg-light-blue hover-bg-lightest-blue hover-gray bn br-pill " type="text" />
        </div>
        <div className="mt3">
            <label className="db f6 ph2 mb2">Author</label>
            <input className="db w-100 b pv2 ph3 bg-light-blue hover-bg-lightest-blue hover-gray bn br-pill " type="text" />
        </div>
        <div className="mt3">
            <label className="db f6 ph2 mb2">Price</label>
            <input className="db w-100 b pv2 ph3 bg-light-blue hover-bg-lightest-blue hover-gray bn br-pill " type="text" />
        </div>

        <div className="mt3">
        <select className="db w-100 b pv2 ph3 bg-light-blue hover-bg-lightest-blue hover-grey bn br-pill pa2 input-reset">
            <option value="not-read">Not read</option>
            <option value="read">Read</option>

        </select>
        </div>
        <div className="mt3">
            <input className="input-reset db w-100 f6 b ttu tracked pv3 ph3 pointer bg-light-blue hover-bg-lightest-blue bn br-pill" type="submit" value="Add book"/>
        </div>
    </form>
    );
    }
}

export default AddBooks;
