import React, { Component } from 'react';
import './App.css';

class Login extends Component {
    render(){
        return(
                <form className="measure center bg-light-blue" >
                        <h1 className="coolFont ">Sign in</h1>
                        <div className="mt3">
                            <label htmlFor="username" className="db f6 ph2 mb2">Username</label>
                            <input type="text" name="username" className="input-reset db w-100 mw-100 white b pv2 ph3 bg-white-30 hover-bg-white-70 hover-gray outline-0 bn br-pill"/>
                        </div>
                        <div className="mv3">
                            <label htmlFor="password" className="db f6 ph2 mb2" >Password</label>
                            <input type="password" name="password"
                                   className="input-reset db w-100 mw-100 white b pv2 ph3 bg-white-30 hover-bg-white-70 hover-gray outline-0 bn br-pill"/>
                        </div>

                    <button className="input-reset db w-100 light-gray f6 b ttu tracked pv3 ph3 pointer bg-blue hover-bg-light-blue bn br-pill">Sign In</button>
                        <div className="lh-copy mt3">
                            <a href="#0" className="f6 link dim black db">Sign up</a>
                        </div>
                </form>

        );
    }
}

export default Login;
